import React, { useState } from "react";
import Button from "@mui/material/Button";
import AddIcon from "@mui/icons-material/Add";

const CreateNote = () => {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  const InputEvent = () => {
    setNote((prevData) => {
        return{
            ...prevData,
        }
    });
  };

  return (
    <div className="main_note">
      <from>
        <input
          type="text"
          value={note.title}
          onChange={InputEvent}
          placeholder="Title"
          autoComplete="off"
        />
        <textarea
          rows=""
          column={note.content}
          value=""
          onChange={InputEvent}
          placeholder="Write a note..."
        ></textarea>

        <Button>
          <AddIcon className="plus_sign" />
        </Button>
      </from>
    </div>
  );
};

export default CreateNote;
