import React, { useState } from "react";

import "./App.css";

function App() {
  const purple = "#8e44ad";

  const [bg, setBg] = useState(purple);
  const [name, setName] = useState("Click Me");
  const bgChange = () => {
    const newBg = "#34495e";
    setBg(newBg);
    setName("Ouch!! üê∫");
  };

  return (
    <>
      <div style={{ backgroundColor: bg }}>
        <button onClick={bgChange}> {name} </button>
      </div>
    </>
  );
}

export default App;
