import React, { useEffect, useState } from "react";
import axios from "axios";

const CompA = () => {
  const [num, setNum] = useState(1); // Initialize with a default value
  const [pokemonData, setPokemonData] = useState(null); // For storing API response

  useEffect(() => {
    async function getData() {
      try {
        const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`);
        setPokemonData(res.data); // Axios automatically parses the JSON response
        console.log(res.data);
      } catch (error) {
        console.error("Error fetching data:", error); // Handle the error properly
      }
    }
    getData();
  }, [num]); // Dependency array ensures useEffect runs when 'num' changes

  return (
    <>
      <h1>You chose {num} value</h1>
      <h2>Pokemon name: {pokemonData?.name}</h2> {/* Display fetched data */}
      <select
        value={num}
        onChange={(event) => {
          setNum(event.target.value);
        }}
      >
        <option value="1">1</option>
        <option value="25">25</option>
        <option value="3">3</option>
        <option value="4">4</option>
        <option value="5">5</option>
      </select>
    </>
  );
};

export default CompA;
